import { Card } from "../Card";
import { Cards } from "../Cards/Cards";
import "./Search.css";
import React from "react";
import axios from "axios";

enum Category {
  all,
  movies,
  series,
}

async function search_for(input: string, cat: Category): Promise<Card[]> {
  try {
    let type = ''
    switch (cat) {
        case Category.movies: type = 'movie'; break;
        case Category.series: type = 'series'; break;
      }
    const response = await axios.get("https://www.omdbapi.com/", {
      params: {
        apikey: "d1f715ef",
        s: input,
        type: Category[cat],
      },
    });

    if (response.data.Response === "True") {
      const cards: Card[] = response.data.Search.map((item: any) => ({
        title: item.Title,
        year: parseInt(item.Year),
        type: cat,
        image: item.Poster,
        url: `https://www.imdb.com/title/${item.imdbID}/`,
      }));

      return cards;
    } else {
      return [];
    }
  } catch (error) {
    console.error(error);
    return [];
  }
}

export function Search() {
  let [cards, setCards] = React.useState([] as Card[]);
  let [category, setCategory] = React.useState(Category.all);
  let [input, setInput] = React.useState("");

  console.log(input);

  return (
    <div className="Search">
      <input
        className="Search-input"
        placeholder="search"
        onChange={(v) => setInput(v.target.value)}
      />
      <div className="Search-props">
        <label>
          <input
            type="radio"
            name="searchType"
            onChange={() => setCategory(Category.all)}
          />{" "}
          All
        </label>
        <label>
          <input
            type="radio"
            name="searchType"
            onChange={() => setCategory(Category.movies)}
          />{" "}
          Movies only
        </label>
        <label>
          <input
            type="radio"
            name="searchType"
            onChange={() => setCategory(Category.series)}
          />{" "}
          Series only
        </label>

        <button
          className="Search-button"
          onClick={async () => setCards(await search_for(input, category))}
        >
          search
        </button>
      </div>
      <Cards cards={cards} />
    </div>
  );
}
