import { Card } from "../Card";
import { Cards } from "../Cards/Cards";
import "./Search.css";
import React from "react";

enum Category {
  all,
  movies,
  series,
}

function search_for(input: string, cat: Category): Card[] {
  return [];
}

export function Search() {
  let [cards, setCards] = React.useState([] as Card[]);
  let [category, setCategory] = React.useState(Category.all);
  let [input, setInput] = React.useState("");

  return (
    <div className="Search">
      <input className="Search-input" placeholder="search" content={input}/>
      <div className="Search-props">
        <label>
          <input type="radio" name="searchType" onChange={() => setCategory(Category.all)} /> All
        </label>
        <label>
          <input type="radio" name="searchType" onChange={() => setCategory(Category.movies)}/> Movies only
        </label>
        <label>
          <input type="radio" name="searchType" onChange={() => setCategory(Category.series)}/> Series only
        </label>

        <button className="Search-button" onClick={() => cards = search_for(input, category)}>search</button>
      </div>
      <Cards cards={cards} />
    </div>
  );
}
