import "./App.css";
import { Menu } from "./Menu/Menu";
import { Footer } from "./Footer/Footer";
import { useState } from "react";
import { Card, Category } from "./Card";
import { Cards } from "./Cards/Cards";
import cock_png from "./cock.png"
import fish_png from "./fish.jpg"
import mmm_png from "./mmm.png"

const cock_card: Card = {
  title: "Cock",
  year: 2024,
  type: Category.movie,
  image: cock_png
}

const fish_card: Card = {
  title: "fish",
  year: 2042,
  type: Category.cartoon,
  image: fish_png
}

const movie_cards: Card[] = [
  cock_card,
  cock_card,
  cock_card,
  cock_card,
  cock_card,
  cock_card,
]

const fish_cards: Card[] = [
  fish_card,
  fish_card,
  fish_card,
  fish_card,
  fish_card,
  fish_card,
]

function App() {
  let [cards, setCards] = useState<Card[]>(movie_cards);

  // predefined categories
  function categoryClicked(cat: string) {
    switch (cat) {
      case "movie":
        setCards(movie_cards)
        break
      case "cartoon":
        setCards(fish_cards)
        break
      case "series":
        setCards(fish_cards)
        break
    }
  }

  return (
    <div className="App">
      <Menu
        items={Object.keys(Category).filter((item) => isNaN(Number(item)))}
        clicked={categoryClicked}
      />
      <Cards cards={cards}/>
      <Footer />
    </div>
  );
}

export default App;
